<?php session_start();date_default_timezone_set('Asia/Calcutta');	//error_reporting(0);		$host="localhost";	$user="root";	$password="";	$database="CT_CManager"; 		$conn = new mysqli($host, $user, $password,$database);	if ($conn->connect_errno) {		printf("Connect failed: %s\n", $conn->connect_error);		exit();	} /* else{		echo "not connect";	} */	class DBHandler{	function runQuery($conn,$query) {		//$conn->open();		$result = $conn->query($query);			while($row=$result->fetch_assoc()) {			$resultset[] = $row;		} 			//$conn->close();		if(!empty($resultset))			return $resultset;	}			function numRows($conn,$query) {		//$conn->open();		$result  = $conn->query($query);		$rowcount = $result->num_rows;		//$conn->close();		return $rowcount;		}	function insertQuery($conn,$query) {		$result = $conn->query($query);				if(!empty($result))		return $result;	}	function updateQuery($conn,$query) {		$result = $conn->query($query);				if(!empty($result))		return $result;	}	function deleteQuery($conn,$query) {		$result = $conn->query($query);				if(!empty($result))		return $result;	}	function CheckQuery($conn,$query) {		$result  = $conn->query($query);		return $result;		}	}?>